    fun getVenueDetails(venueId: String): Venue? {
        return _venues.value.find { it.id == venueId }
    }

    fun getBandDetails(bandId: String): Band? {
        return _bands.value.find { it.id == bandId }
    }

    fun getRelatedBands(bandId: String): List<Band> {
        val band = getBandDetails(bandId)
        return if (band != null) {
            _bands.value.filter { it.genre == band.genre && it.id != bandId }
        } else {
            emptyList()
        }
    }

    fun getReviewsForBand(bandId: String): List<String> {
        // Mock reviews
        return listOf("Great show!", "Awesome energy!", "A must-see band.")
    }


    fun getBandsForVenue(venueId: String): List<Band> {
        // In a real app, you would fetch this data from a remote server or local database.
        return emptyList()
    }

    fun getBookmarkedVenues(): List<Venue> {
        // In a real app, you would fetch this data from a remote server or local database.
        return emptyList()
    }

    fun getBookmarkedBands(): List<Band> {
        // In a real app, you would fetch this data from a remote server or local database.
        return emptyList()
    }

    fun addBookmark(item: Any) {
        when (item) {
            is Venue -> {
