    fun getVenueDetails(venueId: String): Venue? {
        return _venues.value.find { it.id == venueId }
    }

    fun getBandDetails(bandId: String): Band? {
        return _bands.value.find { it.id == bandId }
    }

    fun getRelatedBands(bandId: String): List<Band> {
        val band = getBandDetails(bandId)
        return if (band != null) {
            _bands.value.filter { it.genre == band.genre && it.id != bandId }
        } else {
            emptyList()
        }
    }

    fun getReviewsForBand(@Suppress("UNUSED_PARAMETER") bandId: String): List<String> {
        // Mock reviews
        return listOf("Great show!", "Awesome energy!", "A must-see band.")
    }


    fun getBandsForVenue(venueId: String): List<Band> {
        // In a real app, you would fetch this data from a remote server or local database.
        return emptyList()
    }

    fun getBookmarkedVenues(): List<Venue> {
        // In a real app, you would fetch this data from a remote server or local database.
        return emptyList()
    }

    fun getBookmarkedBands(): List<Band> {
        // In a real app, you would fetch this data from a remote server or local database.
        return emptyList()
    }

    fun addBookmark(item: Any) {
        when (item) {
            is Venue -> {
                // Add venue to bookmarks
            }
            is Band -> {
                // Add band to bookmarks
            }
        }
    }

    fun removeBookmark(item: Any) {
        when (item) {
            is Venue -> {
                // Remove venue from bookmarks
            }
            is Band -> {
                // Remove band from bookmarks
            }
        }
    }
}

package com.example.pitpulseandroid.navigation

/**
 * Type-safe route definitions for app navigation.
 */
sealed class Screen(val route: String) {
    object Home : Screen("home")
    object Venues : Screen("venues")
    object Bands : Screen("bands")
    object Profile : Screen("profile")
    class VenueDetail(venueId: String) : Screen("venue/$venueId") {
        companion object {
            const val route = "venue/{venueId}"
            const val venueIdArg = "venueId"
        }
    }

    class BandDetail(bandId: String) : Screen("band/$bandId") {
        companion object {
            const val route = "band/{bandId}"
            const val bandIdArg = "bandId"
        }
    }
}

package com.example.pitpulseandroid.navigation

/**
 * Type-safe route definitions for app navigation.
 */
sealed class Screen(val route: String) {
    object Home : Screen("home")
    object Venues : Screen("venues")
    object Bands : Screen("bands")
    object Profile : Screen("profile")
    object Badges : Screen("badges")

    class VenueDetail(venueId: String) : Screen("venue/$venueId") {
        companion object {
            const val route = "venue/{venueId}"
            const val venueIdArg = "venueId"
        }
    }

    class BandDetail(bandId: String) : Screen("band/$bandId") {
        companion object {
            const val route = "band/{bandId}"
            const val bandIdArg = "bandId"
        }
    }
}

