package com.example.pitpulseandroid.ui.home

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.material3.*
import androidx.compose.runtime.Composable
import androidx.compose.runtime.collectAsState
import androidx.compose.runtime.getValue
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.unit.dp
import androidx.lifecycle.viewmodel.compose.viewModel
import com.example.pitpulseandroid.data.repository.Repository
import com.example.pitpulseandroid.ui.components.SectionHeader

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun HomeScreen(
    modifier: Modifier = Modifier,
    viewModel: HomeViewModel = viewModel(factory = HomeViewModelFactory(Repository())),
    onNavigateToVenue: (String) -> Unit = {}
) {
    val uiState by viewModel.uiState.collectAsState()

    Column(
        modifier = modifier.fillMaxSize()
    ) {
        when {
            uiState.isLoading -> {
                Box(
                    modifier = Modifier.fillMaxSize(),
                    contentAlignment = Alignment.Center
                ) {
                    CircularProgressIndicator()
                }
            }

            uiState.error != null -> {
                ErrorMessage(
                    error = uiState.error!!,
                    onRetry = { viewModel.retryLoading() }
                )
            }

            else -> {
                HomeContent(
                    onNavigateToVenue = onNavigateToVenue,
                    onRefresh = { viewModel.refreshData() }
                )
            }
        }
    }
}

@Composable
private fun ErrorMessage(
    error: String,
    onRetry: () -> Unit,
    modifier: Modifier = Modifier
) {
    Column(
        modifier = modifier
            .fillMaxSize()
            .padding(16.dp),
        horizontalAlignment = Alignment.CenterHorizontally,
        verticalArrangement = Arrangement.Center
    ) {
        Text(
            text = error,
            style = MaterialTheme.typography.bodyLarge,
            color = MaterialTheme.colorScheme.error,
            textAlign = TextAlign.Center
        )

        Spacer(modifier = Modifier.height(16.dp))

        Button(onClick = onRetry) {
            Text("Retry")
        }
    }
}

@Composable
private fun HomeContent(
    onNavigateToVenue: (String) -> Unit,
    onRefresh: () -> Unit,
    modifier: Modifier = Modifier,
    viewModel: HomeViewModel = viewModel(factory = HomeViewModelFactory(Repository()))
) {
    val featuredVenues by viewModel.getFeaturedVenues().collectAsState()
    val popularBands by viewModel.getPopularBands().collectAsState()

    LazyColumn(
        modifier = modifier.fillMaxSize(),
        contentPadding = PaddingValues(16.dp),
        verticalArrangement = Arrangement.spacedBy(16.dp)
    ) {
        item {
            SectionHeader(title = "Featured Venues")
        }

        item {
            FeaturedVenuesSection(
                venues = featuredVenues,
                onNavigateToVenue = onNavigateToVenue
            )
        }

        item {
            SectionHeader(title = "Popular Bands")
        }

        item {
            PopularBandsSection(bands = popularBands)
        }

        item {
            Button(onClick = onRefresh) {
                Text("Refresh Data")
            }
        }
    }
}

@Composable
fun FeaturedVenuesSection(
    venues: List<Venue>,
    onNavigateToVenue: (String) -> Unit
) {
    if (venues.isEmpty()) {
        Text("No featured venues available")
    } else {
        venues.forEach { venue ->
            Card(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(vertical = 4.dp),
                onClick = { onNavigateToVenue(venue.id) }
            ) {
                Column(modifier = Modifier.padding(16.dp)) {
                    Text(
                        text = venue.name,
                        style = MaterialTheme.typography.headlineSmall
                    )
                    Text(
                        text = venue.location,
                        style = MaterialTheme.typography.bodyMedium
                    )
                }
            }
        }
    }
}

@Composable
fun PopularBandsSection(bands: List<Band>) {
    if (bands.isEmpty()) {
        Text("No popular bands available")
    } else {
        bands.forEach { band ->
            Card(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(vertical = 4.dp)
            ) {
                Column(modifier = Modifier.padding(16.dp)) {
                    Text(
                        text = band.name,
                        style = MaterialTheme.typography.headlineSmall
                    )
                    Text(
                        text = band.genre,
                        style = MaterialTheme.typography.bodyMedium
                    )
                }
            }
        }
    }
}

package com.example.pitpulseandroid.ui.home

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.LazyRow
import androidx.compose.foundation.lazy.items
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.Notifications
import androidx.compose.material.icons.filled.Refresh
import androidx.compose.material3.*
import androidx.compose.runtime.Composable
import androidx.compose.runtime.collectAsState
import androidx.compose.runtime.getValue
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.tooling.preview.Preview
import androidx.compose.ui.unit.dp
import androidx.lifecycle.viewmodel.compose.viewModel
import com.example.pitpulseandroid.data.model.Band
import com.example.pitpulseandroid.data.model.Venue
import com.example.pitpulseandroid.data.repository.Repository
import com.example.pitpulseandroid.ui.components.BandCard
import com.example.pitpulseandroid.ui.components.SectionHeader
import com.example.pitpulseandroid.ui.components.VenueCard
import com.example.pitpulseandroid.ui.theme.PitPulseAndroidTheme

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun HomeScreen(
    onVenueClick: (Venue) -> Unit = {},
    onBandClick: (Band) -> Unit = {},
    onUserClick: () -> Unit = {},
    onNotificationClick: () -> Unit = {},
    homeViewModel: HomeViewModel = viewModel(factory = HomeViewModelFactory(Repository()))
) {
    val uiState by homeViewModel.uiState.collectAsState()
    val featuredVenues by homeViewModel.featuredVenues.collectAsState()
    val popularBands by homeViewModel.popularBands.collectAsState()

    Scaffold(
        topBar = {
            TopAppBar(
                title = { Text("PitPulse") },
                actions = {
                    IconButton(onClick = { homeViewModel.refreshData() }) {
                        Icon(
                            imageVector = Icons.Default.Refresh,
                            contentDescription = "Refresh"
                        )
                    }
                    IconButton(onClick = onNotificationClick) {
                        Icon(
                            imageVector = Icons.Default.Notifications,
                            contentDescription = "Notifications"
                        )
                    }
                }
            )
        }
    ) { paddingValues ->
        when {
            uiState.isLoading -> {
                Box(
                    modifier = Modifier
                        .fillMaxSize()
                        .padding(paddingValues),
                    contentAlignment = Alignment.Center
                ) {
                    CircularProgressIndicator()
                }
            }

            uiState.error != null -> {
                ErrorMessage(
                    error = uiState.error!!,
                    onRetry = { homeViewModel.retryLoading() },
                    modifier = Modifier.padding(paddingValues)
                )
            }

            else -> {
                LazyColumn(
                    modifier = Modifier
                        .fillMaxSize()
                        .padding(paddingValues),
                    verticalArrangement = Arrangement.spacedBy(16.dp),
                    contentPadding = PaddingValues(16.dp)
                ) {
                    item {
                        SectionHeader(
                            title = "Featured Venues",
                            onActionClick = { /* Navigate to all venues */ }
                        )
                    }
                    item {
                        VenueRow(
                            venues = featuredVenues,
                            onVenueClick = onVenueClick
                        )
                    }
                    item {
                        SectionHeader(
                            title = "Popular Bands",
                            onActionClick = { /* Navigate to all bands */ }
                        )
                    }
                    items(popularBands) { band ->
                        BandCard(band = band, onBandClick = onBandClick)
                    }
                }
            }
        }
    }
}

@Composable
private fun ErrorMessage(
    error: String,
    onRetry: () -> Unit,
    modifier: Modifier = Modifier
) {
    Column(
        modifier = modifier
            .fillMaxSize()
            .padding(16.dp),
        horizontalAlignment = Alignment.CenterHorizontally,
        verticalArrangement = Arrangement.Center
    ) {
        Text(
            text = error,
            style = MaterialTheme.typography.bodyLarge,
            color = MaterialTheme.colorScheme.error,
            textAlign = TextAlign.Center
        )

        Spacer(modifier = Modifier.height(16.dp))

        Button(onClick = onRetry) {
            Text("Retry")
        }
    }
}

@Composable
fun VenueRow(
    venues: List<Venue>,
    onVenueClick: (Venue) -> Unit
) {
    LazyRow(
        horizontalArrangement = Arrangement.spacedBy(16.dp)
    ) {
        items(venues) { venue ->
            VenueCard(
                venue = venue,
                onClick = { onVenueClick(venue) }
            )
        }
    }
}

@Suppress("unused")
@Preview(showBackground = true)
@Composable
fun HomeScreenPreview() {
    PitPulseAndroidTheme {
        HomeScreen()
    }
}

package com.example.pitpulseandroid.ui.home

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.pitpulseandroid.data.model.Band
import com.example.pitpulseandroid.data.model.User
import com.example.pitpulseandroid.data.model.Venue
import com.example.pitpulseandroid.data.repository.Repository
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch

class HomeViewModel(
    private val repository: Repository = Repository()
) : ViewModel() {

    private val _uiState = MutableStateFlow(HomeUiState())
    val uiState: StateFlow<HomeUiState> = _uiState.asStateFlow()

    private val _featuredVenues = MutableStateFlow<List<Venue>>(emptyList())
    val featuredVenues: StateFlow<List<Venue>> = _featuredVenues.asStateFlow()

    private val _popularBands = MutableStateFlow<List<Band>>(emptyList())
    val popularBands: StateFlow<List<Band>> = _popularBands.asStateFlow()

    init {
        loadInitialData()
    }

    private fun loadInitialData() {
        viewModelScope.launch {
            _uiState.value = _uiState.value.copy(isLoading = true)
            try {
                // Use the repository property
                val venues = repository.getFeaturedVenues()
                _featuredVenues.value = venues

                val bands = repository.getPopularBands()
                _popularBands.value = bands

                _uiState.value = _uiState.value.copy(isLoading = false, error = null)
            } catch (e: Exception) {
                _uiState.value = _uiState.value.copy(isLoading = false, error = e.message ?: "Unknown error occurred")
            }
        }
    }

    fun retryLoading() {
        loadInitialData()
    }

    fun refreshData() {
        loadInitialData()
    }

    fun clearError() {
        _uiState.value = _uiState.value.copy(error = null)
    }

    fun getFeaturedVenues() = featuredVenues

    fun getPopularBands() = popularBands
}

data class HomeUiState(
    val isLoading: Boolean = false,
    val error: String? = null
)

package com.example.pitpulseandroid.ui.home

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.pitpulseandroid.data.model.Band
import com.example.pitpulseandroid.data.model.Venue
import com.example.pitpulseandroid.data.repository.Repository
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.catch
import kotlinx.coroutines.flow.launchIn
import kotlinx.coroutines.flow.onEach
import kotlinx.coroutines.launch

/**
 * ViewModel for the Home screen.
 */
class HomeViewModel(private val repository: Repository) : ViewModel() {

    private val _uiState = MutableStateFlow(HomeUiState())
    val uiState: StateFlow<HomeUiState> = _uiState.asStateFlow()

    private val _featuredVenues = MutableStateFlow<List<Venue>>(emptyList())
    val featuredVenues: StateFlow<List<Venue>> = _featuredVenues.asStateFlow()

    private val _popularBands = MutableStateFlow<List<Band>>(emptyList())
    val popularBands: StateFlow<List<Band>> = _popularBands.asStateFlow()

    init {
        loadHomeData()
    }

    private fun loadHomeData() {
        viewModelScope.launch {
            _uiState.value = _uiState.value.copy(isLoading = true)
            repository.initMockData()

            repository.getVenues()
                .onEach { venues ->
                    _featuredVenues.value = venues
                    _uiState.value = _uiState.value.copy(isLoading = false)
                }
                .catch { e ->
                    _uiState.value = _uiState.value.copy(isLoading = false, error = e.message)
                }
                .launchIn(viewModelScope)

            repository.getBands()
                .onEach { bands ->
                    _popularBands.value = bands
                }
                .catch { e ->
                    _uiState.value = _uiState.value.copy(isLoading = false, error = e.message)
                }
                .launchIn(viewModelScope)
        }
    }

    fun retryLoading() {
        loadHomeData()
    }

    fun refreshData() {
        loadHomeData()
    }

    fun clearError() {
        _uiState.value = _uiState.value.copy(error = null)
    }
}

/**
 * UI state for the Home screen.
 */
data class HomeUiState(
    val isLoading: Boolean = false,
    val featuredVenues: List<Venue> = emptyList(),
    val popularBands: List<Band> = emptyList(),
    val error: String? = null
)

package com.example.pitpulseandroid.ui.home

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.pitpulseandroid.data.repository.Repository
import com.example.pitpulseandroid.data.model.Band
import com.example.pitpulseandroid.data.model.User
import com.example.pitpulseandroid.data.model.Venue
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.catch
import kotlinx.coroutines.launch

/**
 * ViewModel for the Home screen.
 * This class is used by the Home screen to display venues, bands, and user information.
 */
@Suppress("unused")
class HomeViewModel(private val repository: Repository) : ViewModel() {

    // UI state for the Home screen
    private val _uiState = MutableStateFlow(HomeUiState())

    /**
     * Public accessor for the UI state.
     * This is observed by the Home screen to display content and handle loading/error states.
     */
    @Suppress("unused")
    val uiState: StateFlow<HomeUiState> = _uiState.asStateFlow()

    init {
        // Initialize repository with mock data
        repository.initMockData()
        loadData()
    }

    /**
     * Load all data needed for the Home screen.
     */
    private fun loadData() {
        viewModelScope.launch {
            // Load venues
            repository.getVenues()
                .catch { 
                    _uiState.value = _uiState.value.copy(error = it.message)
                    // Load sample venues as fallback
                    _uiState.value = _uiState.value.copy(
                        venues = getSampleVenues(),
                        isLoading = false
                    )
                }
                .collect { venues ->
                    _uiState.value = _uiState.value.copy(
                        venues = venues,
                        isLoading = false
                    )
                }
        }

        viewModelScope.launch {
            // Load bands
            repository.getBands()
                .catch { 
                    _uiState.value = _uiState.value.copy(error = it.message)
                    // Load sample bands as fallback
                    _uiState.value = _uiState.value.copy(
                        bands = getSampleBands(),
                        isLoading = false
                    )
                }
                .collect { bands ->
                    _uiState.value = _uiState.value.copy(
                        bands = bands,
                        isLoading = false
                    )
                }
        }

        viewModelScope.launch {
            // Load user
            repository.getCurrentUser()
                .catch { _uiState.value = _uiState.value.copy(error = it.message) }
                .collect { user ->
                    _uiState.value = _uiState.value.copy(
                        user = user,
                        isLoading = false
                    )
                }
        }
    }

    /**
     * Search venues and bands.
     */
    @Suppress("unused")
    fun search(query: String) {
        viewModelScope.launch {
            _uiState.value = _uiState.value.copy(isSearching = true)

            repository.searchVenues(query)
                .catch { _uiState.value = _uiState.value.copy(error = it.message) }
                .collect { venues ->
                    _uiState.value = _uiState.value.copy(
                        venues = venues,
                        isSearching = false
                    )
                }
        }

        viewModelScope.launch {
            repository.searchBands(query)
                .catch { _uiState.value = _uiState.value.copy(error = it.message) }
                .collect { bands ->
                    _uiState.value = _uiState.value.copy(
                        bands = bands,
                        isSearching = false
                    )
                }
        }
    }

    /**
     * Get sample venues data for testing or fallback.
     */
    private fun getSampleVenues(): List<Venue> {
        return listOf(
            Venue(
                id = "v1",
                name = "The Sound Garden",
                address = "123 Main St",
                city = "Brooklyn",
                state = "NY",
                zipCode = "11201",
                description = "A legendary venue known for launching indie bands",
                imageUrl = "https://picsum.photos/id/1035/500/300",
                rating = 4.5f,
                reviewCount = 120,
                capacity = 500,
                contactInfo = "info@soundgarden.com",
                distance = 0.8f
            ),
            Venue(
                id = "v2",
                name = "Electric Avenue",
                address = "456 Park Ave",
                city = "Brooklyn",
                state = "NY",
                zipCode = "11205",
                description = "Modern venue with great acoustics and lighting",
                imageUrl = "https://picsum.photos/id/1036/500/300",
                rating = 4.2f,
                reviewCount = 85,
                capacity = 350,
                contactInfo = "contact@electricavenue.com",
                distance = 1.2f
            ),
            Venue(
                id = "v3",
                name = "The Basement",
                address = "789 Broadway",
                city = "Brooklyn",
                state = "NY",
                zipCode = "11206",
                description = "Underground venue featuring indie bands and craft beer",
                imageUrl = "https://picsum.photos/id/1037/500/300",
                rating = 4.8f,
                reviewCount = 210,
                capacity = 200,
                contactInfo = "basement@music.com",
                distance = 1.5f
            )
        )
    }

    /**
     * Get sample bands data for testing or fallback.
     */
    private fun getSampleBands(): List<Band> {
        return listOf(
            Band(
                id = "b1",
                name = "Electric Harmony",
                genre = "Rock",
                bio = "A four-piece band known for energetic performances",
                imageUrl = "https://picsum.photos/id/1038/500/300",
                formationYear = 2018,
                location = "Brooklyn, NY",
                rating = 4.7f,
                reviewCount = 156,
                genres = listOf("Rock", "Alternative", "Indie"),
                nextShow = "Tonight at The Sound Garden"
            ),
            Band(
                id = "b2",
                name = "Midnight Groove",
                genre = "R&B",
                bio = "Soul collective bringing back classic sounds with a modern twist",
                imageUrl = "https://picsum.photos/id/1039/500/300",
                formationYear = 2016,
                location = "Queens, NY",
                rating = 4.3f,
                reviewCount = 98,
                genres = listOf("R&B", "Soul", "Funk"),
                nextShow = "Tomorrow at Electric Avenue"
            ),
            Band(
                id = "b3",
                name = "Cosmic Travelers",
                genre = "Psychedelic",
                bio = "Experimental space rock outfit pushing musical boundaries",
                imageUrl = "https://picsum.photos/id/1040/500/300",
                formationYear = 2020,
                location = "Manhattan, NY",
