    }
}

/**
 * UI state for the Home screen.
 */
data class HomeUiState(
    val venues: List<Venue> = emptyList(),
    val bands: List<Band> = emptyList(),
    val user: User? = null,
    val isLoading: Boolean = true,
    val isSearching: Boolean = false,
    val error: String? = null
)

