class VenuesViewModel : ViewModel() { private val _searchQuery = MutableStateFlow("") val searchQuery: StateFlow<String> = _searchQuery.asStateFlow()
// Mock data for venues private val allVenues = listOf( Venue( id = "v1", name = "The Sound Garden", image = "https://picsum.photos/id/1035/500/300", rating = 4.5f, reviewCount = 120, address = "123 Main St, Brooklyn, NY", distance = "0.8 mi", amenities = listOf("Live Music", "Full Bar", "Food"), city = "Brooklyn", state = "NY", zipCode = "11201", description = "A legendary venue known for launching indie bands", capacity = 500, contactInfo = "info@soundgarden.com" ), Venue( id = "v2", name = "Electric Avenue", image = "https://picsum.photos/id/1036/500/300", rating = 4.2f, reviewCount = 85, address = "456 Park Ave, Brooklyn, NY", distance = "1.2 mi", amenities = listOf("DJ Booth", "Dance Floor", "Cocktails"), city = "Brooklyn", state = "NY", zipCode = "11205", description = "Modern venue with great acoustics and lighting", capacity = 350, contactInfo = "contact@electricavenue.com" ), Venue( id = "v3", name = "The Basement", image = "https://picsum.photos/id/1037/500/300", rating = 4.8f, reviewCount = 210, address = "789 Broadway, Brooklyn, NY", distance = "1.5 mi", amenities = listOf("Underground", "Indie Bands", "Craft Beer"), city = "Brooklyn", state = "NY", zipCode = "11206", description = "Underground venue featuring indie bands and craft beer", capacity = 200, contactInfo = "basement@music.com" ), Venue( id = "v4", name = "Harmony Hall", image = "https://picsum.photos/id/1041/500/300", rating = 4.4f, reviewCount = 112, address = "234 Fifth Ave, Brooklyn, NY", distance = "2.1 mi", amenities = listOf("Acoustic", "Intimate", "Wine Bar"), city = "Brooklyn", state = "NY", zipCode = "11205", description = "Harmony Hall is an intimate venue perfect for acoustic performances and jazz nights.", capacity = 150, contactInfo = "contact@harmonyhall.com" ), Venue( id = "v5", name = "The Echo Chamber", image = "https://picsum.photos/id/1042/500/300", rating = 4.6f, reviewCount = 189, address = "567 Sixth St, Brooklyn, NY", distance = "1.7 mi", amenities = listOf("Electronic", "Light Show", "Late Night"), city = "Brooklyn", state = "NY", zipCode = "11215", description = "The Echo Chamber is a cutting-edge venue specializing in electronic music and immersive experiences.", capacity = 400, contactInfo = "contact@echochamber.com" ) )
private val _venues = MutableStateFlow(allVenues) val venues: StateFlow<List<Venue>> = _venues.asStateFlow()
fun onSearchQueryChange(query: String) { _searchQuery.value = query
// Filter venues based on search query if (query.isBlank()) { _venues.value = allVenues } else { _venues.value = allVenues.filter { venue -> venue.name.contains(query, ignoreCase = true) || venue.address.contains(query, ignoreCase = true) || venue.amenities.any { it.contains(query, ignoreCase = true) } } } } }

package com.example.pitpulseandroid.util

import androidx.navigation.NavController
import androidx.navigation.NavGraph.Companion.findStartDestination
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.Job
import kotlinx.coroutines.delay
import kotlinx.coroutines.launch

/**
 * Utility functions for navigation.
 */
object NavigationUtils {
    private var lastNavigationTime = 0L
    private var navigationJob: Job? = null
    private const val NAVIGATION_DEBOUNCE_TIME = 300L

    /**
     * Navigate to a destination with single top behavior and debouncing to prevent rapid navigation.
     * 
     * @param route The route to navigate to
     * @param popUpToRoute The route to pop up to, default is null
     * @param inclusive Whether to include the route in pop up to, default is false
     */
    fun NavController.navigateSingleTop(route: String, popUpToRoute: String? = null, inclusive: Boolean = false) {
        val currentTime = System.currentTimeMillis()

        // Prevent rapid multiple navigations
        if (currentTime - lastNavigationTime < NAVIGATION_DEBOUNCE_TIME) {
            return
        }

        lastNavigationTime = currentTime

        // Cancel any pending navigation
        navigationJob?.cancel()

        // Start a new navigation job
        navigationJob = CoroutineScope(Dispatchers.Main).launch {
            delay(50) // Small delay to ensure UI stability
            navigate(route) {
                if (popUpToRoute != null) {
                    popUpTo(popUpToRoute) { this.inclusive = inclusive }
                } else {
                    // Pop up to start destination by default for main tab navigation
                    popUpTo(graph.findStartDestination().id) { this.inclusive = false }
                }
                launchSingleTop = true
                restoreState = true
            }
        }
    }

    /**
     * Safely retrieve a string argument from navigation back stack entry.
     * 
     * @param key The argument key
     * @param defaultValue The default value if argument is null
     * @return The argument value or default value
     */
    fun NavController.getStringArgument(key: String, defaultValue: String = ""): String {
        return currentBackStackEntry?.arguments?.getString(key) ?: defaultValue
    }

    /**
     * Check if current route is equal to given route.
     * 
     * @param route The route to check
     * @return True if current destination route equals given route
     */
        return currentDestination?.route == route
    }
}

package com.example.pitpulseandroid.util

import androidx.navigation.NavController
import androidx.navigation.NavGraph.Companion.findStartDestination
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.Job
import kotlinx.coroutines.delay
import kotlinx.coroutines.launch

/**
 * Utility functions for navigation.
 */
object NavigationUtils {
    private var lastNavigationTime = 0L
    private var navigationJob: Job? = null
    private const val NAVIGATION_DEBOUNCE_TIME = 300L

    /**
     * Navigate to a destination with single top behavior and debouncing to prevent rapid navigation.
     * 
     * @param route The route to navigate to
     * @param popUpToRoute The route to pop up to, default is null
     * @param inclusive Whether to include the route in pop up to, default is false
     */
    fun NavController.navigateSingleTop(route: String, popUpToRoute: String? = null, inclusive: Boolean = false) {
        val currentTime = System.currentTimeMillis()

        // Prevent rapid multiple navigations
        if (currentTime - lastNavigationTime < NAVIGATION_DEBOUNCE_TIME) {
            return
        }

        lastNavigationTime = currentTime

        // Cancel any pending navigation
        navigationJob?.cancel()

        // Start a new navigation job
        navigationJob = CoroutineScope(Dispatchers.Main).launch {
            delay(50) // Small delay to ensure UI stability
            navigate(route) {
                if (popUpToRoute != null) {
                    popUpTo(popUpToRoute) { this.inclusive = inclusive }
                } else {
                    // Pop up to start destination by default for main tab navigation
                    popUpTo(graph.findStartDestination().id) { this.inclusive = false }
                }
                launchSingleTop = true
                restoreState = true
            }
        }
    }

    /**
     * Safely retrieve a string argument from navigation back stack entry.
     * 
     * @param key The argument key
     * @param defaultValue The default value if argument is null
     * @return The argument value or default value
     */
    fun NavController.getStringArgument(key: String, defaultValue: String = ""): String {
        return currentBackStackEntry?.arguments?.getString(key) ?: defaultValue
    }

    /**
     * Check if current route is equal to given route.
     * 
     * @param route The route to check
     * @return True if current destination route equals given route
     */
    @Suppress("unused") // Will be used in future for route-based UI updates
    fun NavController.isCurrentRoute(route: String): Boolean {
        return currentDestination?.route == route
    }
}
