package com.example.pitpulseandroid.data.model
/** * Data class representing a badge that users can earn */ data class Badge( val id: String, val name: String, val description: String, val imageUrl: String, val icon: Int, // Resource ID for the icon val badgeType: BadgeType, val threshold: Int, // Threshold to achieve this badge val dateEarned: String? = null ) { companion object { /** * Returns a list of sample badges for development and testing */ fun getSampleBadges(): List<Badge> { return listOf( Badge( id = "badge1", name = "Venue Explorer", description = "Visit 5 different venues", imageUrl = "", icon = android.R.drawable.ic_menu_compass, badgeType = BadgeType.VENUE_VISIT, threshold = 5 ), Badge( id = "badge2", name = "Review Master", description = "Write 10 reviews", imageUrl = "", icon = android.R.drawable.ic_menu_edit, badgeType = BadgeType.REVIEW, threshold = 10 ), Badge( id = "badge3", name = "Feedback Pro", description = "Receive 15 helpful votes on your reviews", imageUrl = "", icon = android.R.drawable.ic_menu_send, badgeType = BadgeType.REVIEW, threshold = 15 ), Badge( id = "badge4", name = "Night Owl", description = "Attend 3 shows that started after 10pm", imageUrl = "", icon = android.R.drawable.ic_menu_recent_history, badgeType = BadgeType.SPECIAL_EVENT, threshold = 3 ), Badge( id = "badge5", name = "Local Champion", description = "Visit 8 venues in your city", imageUrl = "", icon = android.R.drawable.ic_menu_myplaces, badgeType = BadgeType.VENUE_VISIT, threshold = 8 ) ) } } }
/** * Enum representing different types of badges */ enum class BadgeType { REVIEW, VENUE_VISIT, SPECIAL_EVENT // Removed unused badge types }

package com.example.pitpulseandroid.data.model

/**
 * Data class representing a user's progress towards earning a badge
 */
data class BadgeProgress(
    val badge: Badge,
    val currentValue: Int,
    val isEarned: Boolean = currentValue >= badge.threshold,
    val percentComplete: Float = getProgressPercentage(),
    val tier: String = calculateTier()
) {
    /**
     * Calculate the progress percentage (0-100)
     */
    fun getProgressPercentage(): Float {
        if (isEarned) return 100f
        if (badge.threshold <= 0) return 0f
        return (currentValue.toFloat() / badge.threshold) * 100
    }

    /**
     * Calculate badge tier based on completion percentage
     */
    private fun calculateTier(): String {
        return when {
            isEarned -> "Gold"
            percentComplete >= 66 -> "Silver"
            percentComplete >= 33 -> "Bronze"
            else -> "None"
        }
    }

    companion object {
        /**
         * Creates a list of sample badge progress objects for testing
         */
        fun getSampleBadgeProgress(): List<BadgeProgress> {
            return Badge.getSampleBadges().mapIndexed { index, badge ->
                // Create varied progress for each badge
                val progress = when (index % 3) {
                    0 -> badge.threshold // Completed
                    1 -> (badge.threshold * 0.7).toInt() // In progress
                    else -> (badge.threshold * 0.2).toInt() // Just started
                }
                BadgeProgress(
                    badge = badge,
                    currentValue = progress
                )
            }
        }
    }
}

package com.example.pitpulseandroid.data.model

/**
 * Data class representing a user's progress towards earning a badge
 */
data class BadgeProgress(
    val badge: Badge,
    val currentValue: Int,
    val isEarned: Boolean = currentValue >= badge.threshold
) {
    // Calculate progress percentage (0-100) on demand
    val percentComplete: Float by lazy { getProgressPercentage() }

    // Calculate tier based on completion percentage
    val tier: String by lazy { calculateTier() }

    /**
     * Calculate the progress percentage (0-100)
     */
    private fun getProgressPercentage(): Float {
        if (isEarned) return 100f
        if (badge.threshold <= 0) return 0f
        return (currentValue.toFloat() / badge.threshold) * 100
    }

    /**
     * Calculate badge tier based on completion percentage
     */
    private fun calculateTier(): String {
        val percent = percentComplete
        return when {
            isEarned -> "Gold"
            percent >= 66 -> "Silver"
            percent >= 33 -> "Bronze"
            else -> "None"
        }
    }

    companion object {
        /**
         * Creates a list of sample badge progress objects for testing
         */
        fun getSampleBadgeProgress(): List<BadgeProgress> {
            return Badge.getSampleBadges().mapIndexed { index, badge ->
                // Create varied progress for each badge
                val progress = when (index % 3) {
                    0 -> badge.threshold // Completed
                    1 -> (badge.threshold * 0.7).toInt() // In progress
                    else -> (badge.threshold * 0.2).toInt() // Just started
                }
                BadgeProgress(
                    badge = badge,
                    currentValue = progress
                )
            }
        }
    }
}

package com.example.pitpulseandroid.data.model
/** * Data class representing a music band or artist */ data class Band( val id: String, val name: String, val genre: String, val bio: String, val imageUrl: String, val formationYear: Int, val location: String, val rating: Float = 0f, val reviewCount: Int = 0, val genres: List<String> = listOf(), val nextShow: String = "", val members: List<BandMember> = emptyList(), val upcomingEvents: List<Event> = emptyList() )
/** * Data class representing a band member */ data class BandMember( val id: String, val name: String, val role: String, val imageUrl: String = "" )

package com.example.pitpulseandroid.data.model
