package com.example.pitpulseandroid.data.repository
import com.example.pitpulseandroid.data.model.* import kotlinx.coroutines.flow.Flow import kotlinx.coroutines.flow.MutableStateFlow import kotlinx.coroutines.flow.asStateFlow import kotlinx.coroutines.flow.flow
/** * Repository class that acts as a single source of truth for app data */ class Repository {
// Mock data for development private val _currentUser = MutableStateFlow<User?>(null) private val _venues = MutableStateFlow<List<Venue>>(emptyList()) private val _bands = MutableStateFlow<List<Band>>(emptyList())
// Public accessors val venues = _venues.asStateFlow() val bands = _bands.asStateFlow()
/** * Get a list of venues */ fun getVenues(): Flow<List<Venue>> { // In a real app, this would call an API or database return flow { emit(_venues.value) } }
/** * Get a list of bands */ fun getBands(): Flow<List<Band>> { // In a real app, this would call an API or database return flow { emit(_bands.value) } }
/** * Get the current logged-in user */ fun getCurrentUser(): Flow<User> { // In a real app, this would get from local storage or API return flow { _currentUser.value?.let { emit(it) } ?: throw Exception("User not logged in") } }
/** * Search for venues by name or location */ fun searchVenues(query: String): Flow<List<Venue>> { val filteredVenues = _venues.value.filter { it.name.contains(query, ignoreCase = true) || it.city.contains(query, ignoreCase = true) || it.state.contains(query, ignoreCase = true) } return flow { emit(filteredVenues) } }
/** * Search for bands by name or genre */ fun searchBands(query: String): Flow<List<Band>> { val filteredBands = _bands.value.filter { it.name.contains(query, ignoreCase = true) || it.genre.contains(query, ignoreCase = true) } return flow { emit(filteredBands) } }
/** * Initialize repository with mock data for development */ fun initMockData() { // Initialize mock user _currentUser.value = User( id = "user1", username = "rockfan89", email = "user@example.com", level = 3, reviewCount = 15, badgeCount = 4, badges = listOf( Badge( id = "badge1", name = "Venue Explorer", description = "Visited 5 different venues", imageUrl = "", icon = 0, // Replace with actual resource ID badgeType = BadgeType.VENUE_VISIT, threshold = 5 ), Badge( id = "badge2", name = "Review Master", description = "Wrote 10 reviews", imageUrl = "", icon = 0, // Replace with actual resource ID badgeType = BadgeType.REVIEW, threshold = 10 ) ) )
